mapper.select:
select height, create_date as createDate , tx_count as txCount, hash, ifnull(c_rep,'-') as peerId, prev_hash as prevHash, ifnull(block_size,'0') as blockSize, hash, ifnull(amount,'-') as amount, ifnull(fee,'-') as fee from T_BLOCK_TOTAL where 1=1
AND height = #{height}
AND hash = #{hash}

select T.tx_hash as txHash, T.from_addr as fromAddr, T.to_addr as toAddr, T.amount as amount , ifnull(T.fee ,'-') as fee, ifnull(T.state , 3) as state, ifnull(T.data_type , '-') as dataType, ifnull(T.tx_type, 0) as txType, T.contract_addr as targetContractAddr from T_TRANSACTION_TOTAL as T where 1=1
AND T.height = #{height}
AND T.hash = #{hash}
limit ${page} , ${counting}

select count(*) from T_TRANSACTION_TOTAL where 1=1
AND height = #{height}
AND hash = #{hash}
